import React from 'react';

import addUser from './addUser.js';
import './listUsers.css';
import modify from './images/modify.png';
import del from './images/delete.png';


class listUsers extends React.Component{
		constructor(props) {
    		super(props);

		    this.state = {
		      list: [{Nom: "kerdad", Prenom: "erzer", Email: "", Tel: ""}]
		    };

		  }


		 callAPI(){

		    fetch("http://localhost:9000/test")
		    .then(res => res.json())
		    .then(res => this.setState({list:res.list}));
		  }
		  componentWillMount(){
		    this.callAPI();
		  }
 		delete(index){
 			event.preventDefault();
		      console.log(index);
		      axios.post('http://localhost:9000/test', index)
		      .then(response => console.log(response));
 		}

		render(){
			return(
				<table id="table">
					<thead>
						<tr>

							<td class="column">Nom</td>
							<td class="column">Prénom</td>
							<td class="column">Email</td>
							<td class="column">Téléphone</td>

							<td class="column" id="buttons">Modifier Supprimer</td>
						</tr>
					</thead>

					<tbody>  
					 {this.state.list.map((user, index) => (

						<tr>
							<td class="cell">{user.Nom}</td>
							<td class="cell">{user.Prenom}</td>
							<td class="cell">{user.Email}</td>
							<td class="cell">{user.Tel}</td>

							<td class="cell"><img src={modify} class="icons"  hspace="70"/><img src={del} class="icons" onClick={this.delete(index)} hspace="70"/></td>
						</tr>
					))}
					</tbody> 

				</table>

			);
		}

}


export default listUsers;
