    var me = this;

    fetch('http://localhost:9000/test')
    .accept('json')
    .end(function(err, res){
    if (err) throw err;

      var user = {user: {Nom: res.Nom, Prenom: res.Prenom, Email: res.Email, Tel:res.Tel} }

      console.log('fetch');                  
      req.send(user);  
        });*/
  }

  handleChange(event) {
    const value = event.target.value;
    const name =  event.target.name;
    this.setState({
      [name]: value
    });
  }
  add(){

  	var obj ={Nom:"", Prenom:"", Email:"", Tel:""}
    obj.Nom= this.state.Nom
    obj.Prenom= this.state.Prenom
    obj.Email= this.state.Email
    obj.Tel= this.state.Tel
    console.warn("pchakh", obj)
    /*ReactDOMServer.renderToNodeStream(this.state);*/
    fetch('http://localhost:9000/test', { method : 'POST'})  
    .then((response) => response.json(obj));


  }

  render() {
    return(

		<form method="post"  >

			<div class = "formulaire"><label> <p> Nom : </p> </label><input type="text" name="Nom" value={this.state.Nom} onChange={this.handleChange}/> </div>
			<div class = "formulaire"><label><p> Prénom : </p></label><input type="text" name="Prenom" value={this.state.Prenom} onChange={this.handleChange} /> </div>
			<div class = "formulaire"><label><p>Email : </p> </label><input type="email" name="Email" value={this.state.Email} onChange={this.handleChange} /> </div>
			<div class = "formulaire"><label><p> Téléphone : </p> </label><input type="tel" name="Tel" value={this.state.Tel} onChange={this.handleChange} /> </div>
			<div class="formulaire"><button id="button" type="button" onClick = {this.add}>Ajouter </button>
		</div>
		</form>
	);
  }
}



