import React, {Component} from 'react';

import axios from 'axios';
import './addUser.css';

class addUser extends Component{
  constructor(props) {
    super(props);

    this.state = {
      Nom: "kerdad", Prenom: "erzer", Email: "", Tel: ""
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmitForm = this.handleSubmitForm.bind(this);

  }

  handleSubmitForm(event) {
    event.preventDefault();
      console.log(this.state);
      axios.post('http://localhost:9000/test', this.state)
      .then(response => console.log(response));

}
  handleChange(event) {
    const value = event.target.value;
    const name =  event.target.name;
    this.setState({
      [name]: value
    });
  }




  render() {
    return(

		<form method="post" onSubmit={this.handleSubmitForm} >

			<div class = "formulaire"><label> <p> Nom : </p> </label><input type="text" name="Nom" value={this.state.Nom} onChange={this.handleChange}/> </div>
			<div class = "formulaire"><label><p> Prénom : </p></label><input type="text" name="Prenom" value={this.state.Prenom} onChange={this.handleChange} /> </div>
			<div class = "formulaire"><label><p>Email : </p> </label><input type="email" name="Email" value={this.state.Email} onChange={this.handleChange} /> </div>
			<div class = "formulaire"><label><p> Téléphone : </p> </label><input type="tel" name="Tel" value={this.state.Tel} onChange={this.handleChange} /> </div>
			<div class="formulaire"><button id="button" type="submit">Ajouter </button>
		</div>
		</form>
	);
  }
}







export default addUser;
