import React from 'react';
import ReactDOM from 'react-dom';
import App from './App';
import reportWebVitals from './reportWebVitals';
import addUser from './addUser.js';
import './listUsers.css';
import axios from "axios";


class listUsers extends React.Component{
		constructor(){
			super();
			this.state = {
		      Nom: "kerdad", Prenom: "erzer", Email: "", Tel: ""
		    };
		}
		/*	
		componentDidMount=() =>{
			axios.get("http://localhost:2000/listUsers").then(response=>{
				this.setState({
					Nom: response.data.Nom, Prenom: response.Prenom, Email: response.Email, Telephone: response.Telephone
				});
			});
		}*/

		callAPI(){


			fetch('http://localhost:2000/listUsers')
			.then(res => res.json())
			.then(res =>res.setState({
					Nom: res.data.Nom, Prenom: res.Prenom, Email: res.Email, Telephone: res.Tel
				}));
		}

		componentWillMount(){
			this.callAPI();
		}

		render(){
			return(
				<table id="table">
					<thead>
						<tr>

							<td class="column">Nom</td>
							<td class="column">Prénom</td>
							<td class="column">Email</td>
							<td class="column">Téléphone</td>

							{/*<td class="column" id="buttons">Modifier Supprimer</td>*/}
						</tr>
					</thead>

					<tbody>  
						<tr>
							<td class="cell">{this.state.Nom}</td>
							<td class="cell">{this.state.Prenom}</td>
							<td class="cell">{this.state.Email}</td>
							<td class="cell">{this.state.Tel}</td>

							{/*<td class="cell"><img src="images/modify.png" class="icons"  hspace="70"/><img src="images/delete.png" class="icons" hspace="70"/></td>*/}
						</tr>

					</tbody> 

				</table>

			);
		}

}


export default listUsers;
